RSpec.describe 'test' do
  it 'test with "4\n3 4 2 2 4 4\n5 6 2 3 2 3\n1 1000000000 0 0 1000000000 1000000000\n1000000000 1000000000 500000000 500000000 1000000000 1000000000\n"' do
    io = IO.popen("ruby abc258/F.rb", "w+")
    io.puts("4\n3 4 2 2 4 4\n5 6 2 3 2 3\n1 1000000000 0 0 1000000000 1000000000\n1000000000 1000000000 500000000 500000000 1000000000 1000000000\n")
    io.close_write
    expect(io.readlines.join).to eq("10\n0\n2000000000\n500000000500000000\n")
  end

